import React, {useState} from "react";



export const Options = (props) => {
 const handleClick=()=>{
   props.addOption();
 }

  return (
      <div>
        <label>Options: 
          <input placeholder="Type Answer here" onChange={props.handleInput}></input>
          <button onClick={handleClick}>+</button>
          <button>-</button>
        </label>

      </div>
  );
};
export default Options;
