import React, {useState} from "react";



export const Options = (props) => {
 const handleAdd=()=>{
   props.addOption();
 }
 const handleDel=(event)=>{
   console.log(event)
  props.delOption(event.target.key);
}

  return (
      <div>
        <label>Options: 
          <input placeholder="Type Answer here" onChange={props.handleInput}></input>
          <button onClick={handleAdd}>+</button>
          <button onClick={handleDel} id={props.key}>-</button>
        </label>

      </div>
  );
};
export default Options;
