import React, {useState} from "react";
import { Options } from "./Options";
import {Question} from "./Question";
import {TypeSelector} from "./TypeSelector";

export const CreateSurvey=()=>{
  const [optionsArray, setOptionsArray]=useState([{value:"", id:"1"}, {value:"", id:"2"}]);
  const handleInput=(event)=>{
    optionsArray.find((ele)=>ele.id===event.target.id).value=event.target.value
  
  }
  const generateId=()=>{
      let number=Math.floor(Math.random()*1000);
      return number;
  }
  const delOption=(id)=>{
    if(optionsArray.length>2){
      //console.log(optionsArray);
      const temp=optionsArray.filter((ele)=>ele.id!==id);
      setOptionsArray(temp);
    }

  }

  const addOption=()=>{
    const obj={
      value:"",
      id:""+generateId()
    }
    let temp=[...optionsArray, obj];
    setOptionsArray(temp);
  }
  
  return (
    <>
    <TypeSelector/>
    <br/>
    <Question/>
    <br/>
    {optionsArray.map((item)=>
      <Options id={item.id} handleInput={handleInput} addOption={addOption} delOption={delOption} key={item.id}/>
    )}
    
    </>
  )
}
