import "./styles.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import { Menu } from "./components/Menu";
import { CreateSurvey } from "./components/CreateSurvey";
import { TakeSurvey } from "./components/TakeSurvey";
import {useState} from "react";
import SampleQue from "./SampleQuestion";
export default function App(props) {
  const [qbank, setQbank] = useState([props.SampleQue]);

  const addQuestion=(queString, optionsArray)=>{
    let obj={
      question:queString,
      options:optionsArray
    }
    let temp =[...qbank, obj];
    setQbank(temp);
    console.log(qbank);
  }
  return (
    <div className="App">

      <br/>
      <hr/>
      <Router>
        <div className="backgroung">
          <Link to="/"><img alt="Header image" 
            src="./images/BackgroundImg.png"/>
          </Link>
        </div>

        <Switch>
          <Route exact path="/" component={Menu}/>
          <Route path="/createSurvey" exact><CreateSurvey addQuestion={addQuestion}></CreateSurvey></Route>
          <Route path="/takeSurvey"><TakeSurvey qbank={qbank}></TakeSurvey></Route>
        </Switch>
      </Router>
    </div>
  );
}
