import "./styles.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import { Menu } from "./components/Menu";
import { CreateSurvey } from "./components/CreateSurvey";
import { TakeSurvey } from "./components/TakeSurvey";
import {useState} from "react";
export default function App() {
  const [qbank, setQbank] = useState([]);

  const addQuestion=(queString, optionsArray)=>{
    let obj={
      qustion:queString,
      options:optionsArray
    }
    let temp =[...qbank, obj];
    setQbank(temp);
    console.log(qbank);
  }
  return (
    <div className="App">

      <br/>
      <hr/>
      <Router>
        <Link to="/"><img alt="Header image" 
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqC5qiYRJrI1UIYgCl7xpcfZNNgAdFeo2N_Q&usqp=CAU"/>
        </Link>
        <Switch>
          <Route exact path="/" component={Menu}/>
          <Route path="/createSurvey" exact><CreateSurvey addQuestion={addQuestion}></CreateSurvey></Route>
          <Route path="/takeSurvey"><TakeSurvey qbank={qbank}></TakeSurvey></Route>
        </Switch>
      </Router>
    </div>
  );
}
